import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "../App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const LogoutUser = () => {
    if (window.confirm("You wanna logout?")) {
      localStorage.clear();
      window.location.href = "/login";
    } else {
      window.location.href = "/recipes";
    }
  };

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const auth = localStorage.getItem("token");

  const handleToggleMenu = () => {
    setIsOpen(false);
  };

  return (
    <nav>
      <div className="nav-left">
        <FontAwesomeIcon
          icon={faBars}
          className="hamburger-icon"
          onClick={toggleMenu}
          style={isOpen ? { transform: "rotate(90deg)" } : {}}
        />
        <h2>Recipe Sharing App</h2>
      </div>
      <div className={`nav-right ${isOpen ? "open" : ""}`}>
        <ul>
          {auth ? (
            <>
              <li>
                <NavLink to="/recipes" onClick={handleToggleMenu}>
                  Recipes
                </NavLink>
              </li>

              <li>
                <NavLink to="/add-recipe" onClick={handleToggleMenu}>
                  Add Recipe
                </NavLink>
              </li>

              <li>
                <NavLink to="/liked-products" onClick={handleToggleMenu}>
                  Favourites
                </NavLink>
              </li>

              <li>
                <NavLink to="/login" onClick={LogoutUser}>
                  Logout
                </NavLink>
              </li>
            </>
          ) : (
            <>
              <li>
                <NavLink to="/login" onClick={handleToggleMenu}>
                  Login
                </NavLink>
              </li>

              <li>
                <NavLink to="/register" onClick={handleToggleMenu}>
                  SignUp
                </NavLink>
              </li>

              <li>
                <NavLink to="/forgot-password" onClick={handleToggleMenu}>
                  Forgot Password
                </NavLink>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
